;+function($){
	$.fn.theBanner=function(banner_selector,options){
		new Banner(banner_selector,options,this);
	}
	
	function Banner(banner_selector,options,base_ele){

		this.init(banner_selector,options,base_ele);
	}
	Banner.prototype={
		constructor:Banner,
		init:function(banner_selector,options,base_ele){
			this.index=0;
			this.bannerWrapper=$(banner_selector);
			this.direction=options.direction?options.direction:"fade";
			this.bannerItem=this.bannerWrapper.children();
			this.bannerNum=this.bannerItem.length;
			this.pagination=$(options.pagination?options.pagination.el:"");
			if(this.pagination.length!==0){
				for (let i=0;i<this.bannerNum;i++) {
					var span=$("<span></span>");
					this.pagination.append(span);
					if(i==this.index){
						span.addClass("banner-active");
					}
				}
				this.paginationItem=this.pagination.children();
				this.paginationItem.on("click",{"turn":"toindex"},$.proxy(this.change_index,this));
				this.paginationItem.on("click",$.proxy(this.animation,this));
			}
//			是否有按钮以及按钮元素后续
            if(typeof options.navigation=="object"){
            	this.btnPrev=$(options.navigation.prevEl);
            	this.btnNext=$(options.navigation.nextEl);
            	console.log(this.btnNext);
            	console.log(this.btnPrev);
            	this.btnPrev
            	.on("click",{"turn":"prev"},$.proxy(this.change_index,this))
            	.on("click",$.proxy(this.animation,this));
            	this.btnNext
            	.on("click",{"turn":"next"},$.proxy(this.change_index,this))
            	.on("click",$.proxy(this.animation,this));
            }
		},
		change_index:function(event){
			var turnList={
				"prev":function(){
					this.hidden=this.index;
					if(this.index==0){
						this.index=this.bannerNum-1;
					}else{
						this.index--;
					}
				}.bind(this),
				"next":function(){
					this.hidden=this.index;
					if(this.index==this.bannerNum-1){
						this.index=0;
					}else{
						this.index++;
					}
				}.bind(this),
				"toindex":function(){
					this.hidden=this.index;
					this.index=$(event.target).index();
				}.bind(this)
			}
			turnList[event.data.turn]();
			console.log(this.hidden,this.index);
		},
		animation:function(event){
			if(this.hidden==this.index) return;
			var animationList={
				"slide":function(){
					animationList.slideFadeInit();
					this.bannerItem.eq(this.index)
					.addClass("banner-active")
					.slideDown()
					.siblings()
					.removeClass("banner-active");
				}.bind(this),
				"fade":function(){
					animationList.slideFadeInit();
					this.bannerItem.eq(this.index)
					.addClass("banner-active")
					.fadeIn()
					.siblings()
					.removeClass("active");
				}.bind(this),
				"scroll":function(){
					this.bannerItem
					.css({
						zIndex:0
					})
					.eq(this.hidden)
					.css({
						zIndex:2
					})
					.end()
					.eq(this.index)
					.css({
						zIndex:2
					})
					if(this.hidden<this.index){
						this.bannerItem
						.eq(this.hidden)
						.animate({
							left:-this.bannerItem.width()
						})
						.end()
						.eq(this.index)
						.animate({
							left:0
						})
					}else{
						this.bannerItem
						.eq(this.hidden)
						.animate({
							left:this.bannerItem.width()
						})
						.end()
						.eq(this.index)
						.animate({
							left:0
						})
					}
				}.bind(this),
				"slideFadeInit":function(){
					this.bannerItem.eq(this.hidden)
					.css({
						zIndex:1
					})
					.siblings()
					.css({
						zIndex:""
					})
				}.bind(this)
			}
			animationList[this.direction]();
			          this.pagination.children().eq(this.index)
            .addClass("banner-active")
            .siblings()
            .removeClass("banner-active");
			
		}
	}
}(jQuery);
